// src/App.js
import React, { useEffect, useMemo, useState } from "react";
import PokemonSelect from "./components/PokemonSelect";
import { dedupeBySpecies, toOptions, humanize, indexById } from "./lib/pokeList";
import { LEAGUE_NAMES, importLeague } from "./Data/leagueFiles";
import gm from "./Data/gamemaster.json";
import {
    buildMoveBook,
    bestOfThree,
    recommendMovesFor,
    dangerMovesFor,
} from "./battleCalc";

/* ---------------- small UI bits ---------------- */
function ShieldPicker({ label, value, onChange, onReset }) {
    const btn = (n) => ({
        padding: "8px 14px",
        border: "1px solid #111",
        borderRadius: 10,
        background: value === n ? "#111" : "#fff",
        color: value === n ? "#fff" : "#111",
        cursor: "pointer",
        marginRight: 8,
        minWidth: 40,
        fontWeight: 700,
    });
    const resetBtn = {
        padding: "6px 10px",
        border: "1px solid #666",
        borderRadius: 10,
        cursor: "pointer",
        marginLeft: 8,
        background: "#f7f7f7",
    };
    return (
        <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
            <span style={{ fontWeight: 700, marginRight: 8 }}>{label}</span>
            <button style={btn(0)} onClick={() => onChange(0)}>0</button>
            <button style={btn(1)} onClick={() => onChange(1)}>1</button>
            <button style={btn(2)} onClick={() => onChange(2)}>2</button>
            <button style={resetBtn} onClick={onReset}>Reset</button>
        </div>
    );
}

function Card({ children }) {
    return (
        <div
            style={{
                border: "1px solid #e5e5e5",
                borderRadius: 14,
                padding: 14,
                background: "#fff",
                boxShadow: "0 1px 2px rgba(0,0,0,.04)",
            }}
        >
            {children}
        </div>
    );
}

function Row({ children, style }) {
    return (
        <div
            style={{
                display: "grid",
                gridTemplateColumns: "1fr 1fr",
                gap: 16,
                alignItems: "start",
                ...style,
            }}
        >
            {children}
        </div>
    );
}

/* ---------------- helpers ---------------- */
const emptyPick = () => ({ speciesId: "", name: "", fastMove: undefined, chargedMoves: [] });

function normalizePick(p, leagueEntryMap) {
    if (!p?.speciesId && !p?.name) return p;
    const sid = p.speciesId || p.name;
    const leagueEntry = leagueEntryMap.get(String(sid).toLowerCase());
    const rec = recommendMovesFor(sid, leagueEntry || {});
    return {
        ...p,
        speciesId: sid,
        name: p.name || sid,
        fastMove: p.fastMove || rec.fastMove,
        chargedMoves: p.chargedMoves?.length ? p.chargedMoves : (rec.chargedMoves || []),
    };
}

function hpBarStyle(mePct, foePct, win) {
    return {
        container: {
            height: 6,
            background: "#eee",
            borderRadius: 999,
            overflow: "hidden",
            position: "relative",
            marginTop: 6,
        },
        me: {
            width: `${Math.max(0, Math.min(100, mePct))}%`,
            height: 6,
            background: win ? "#2fbf71" : "#bbb",
        },
        foe: {
            position: "absolute",
            right: 0,
            top: 0,
            height: 6,
            width: `${Math.max(0, Math.min(100, foePct))}%`,
            background: "#f16363",
            opacity: 0.5,
        },
    };
}

/* ---------------- fight card ---------------- */
function FightCard({ fight, titleRight }) {
    const youWon = fight.winner === fight.you;
    const mePct = fight.aHP; // already a percent from your engine adapter
    const foePct = fight.bHP;

    const s = hpBarStyle(mePct, foePct, youWon);

    // Build the "danger move with timing" line for the opponent
    const enemyDanger = fight.bMostDangerous
        ? `Danger move: ${fight.bMostDangerous} - ${fight.bFastMovesToDanger} fasts - ${fight.bTurnsToDanger} turns - ${fight.bSecondsToDanger.toFixed(1)}s`
        : "Danger move: â€”";

    // Optional - my own fastest dangerous move timing
    const myDanger = fight.aMostDangerous
        ? `Your charged to throw: ${fight.aMostDangerous} - ${fight.aFastMovesToDanger} fasts - ${fight.aTurnsToDanger} turns - ${fight.aSecondsToDanger.toFixed(1)}s`
        : (fight.aRecommended ? `Your rec. charged: ${fight.aRecommended}` : "");

    return (
        <Card>
            <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 6 }}>
                <div style={{ fontWeight: 800 }}>
                    vs {fight.vs}
                </div>
                <div style={{ fontSize: 12, opacity: 0.85, fontWeight: 700 }}>
                    {titleRight || ""}
                </div>
            </div>

            <div style={{ fontSize: 12, marginBottom: 6, opacity: 0.8, fontWeight: 600 }}>
                {enemyDanger}
            </div>

            {myDanger && (
                <div style={{ fontSize: 12, marginBottom: 10, opacity: 0.8 }}>
                    {myDanger}
                </div>
            )}

            {/* three little rows like your screenshot */}
            <div style={{ display: "grid", gridTemplateColumns: "1fr", gap: 8 }}>
                {/* Me */}
                <div>
                    <div style={{ fontWeight: 700 }}>
                        {fight.you} <span style={{ marginLeft: 8, color: youWon ? "#2fbf71" : "#e45555" }}>
                            {youWon ? "Win" : fight.winner === "Draw" ? "Draw" : "Loss"}
                        </span>
                    </div>
                    <div style={s.container}>
                        <div style={s.me} />
                        <div style={s.foe} />
                    </div>
                    {fight.aRecommended && (
                        <div style={{ fontSize: 12, marginTop: 6 }}>
                            Your rec. charged:{" "}
                            <span style={{ fontWeight: 700 }}>{fight.aRecommended}</span>
                        </div>
                    )}
                </div>

                {/* Foe - show remaining pct for context */}
                <div style={{ opacity: 0.8, fontSize: 12 }}>
                    Foe remaining hp est.: <span style={{ fontWeight: 700 }}>{foePct}%</span>
                </div>
            </div>
        </Card>
    );
}

/* ---------------- main app ---------------- */
export default function App() {
    // bootstrap moves/species once
    useEffect(() => {
        buildMoveBook(gm);
    }, []);

    const [leagueName, setLeagueName] = useState("Master League");

    // my team and enemy team
    const [me1, setMe1] = useState(emptyPick());
    const [me2, setMe2] = useState(emptyPick());
    const [me3, setMe3] = useState(emptyPick());
    const [foe1, setFoe1] = useState(emptyPick());
    const [foe2, setFoe2] = useState(emptyPick());
    const [foe3, setFoe3] = useState(emptyPick());

    const [myShields, setMyShields] = useState(2);
    const [foeShields, setFoeShields] = useState(2);

    const [excludeMine, setExcludeMine] = useState({}); // {speciesId: true}
    const [excludeFoes, setExcludeFoes] = useState({});

    const league = useMemo(() => importLeague(leagueName), [leagueName]);
    const leagueMap = useMemo(() => {
        const m = new Map();
        (league?.pokemon || []).forEach((p) => {
            m.set(String(p.speciesId || p.name).toLowerCase(), p);
        });
        return m;
    }, [league]);

    // normalize picks with recommended moves if missing
    const myPicks = useMemo(() => {
        return [me1, me2, me3]
            .map((p) => normalizePick(p, leagueMap))
            .filter((p) => (p?.speciesId || p?.name) && !excludeMine[(p.speciesId || p.name)?.toLowerCase()]);
    }, [me1, me2, me3, leagueMap, excludeMine]);

    const foePicks = useMemo(() => {
        return [foe1, foe2, foe3]
            .map((p) => normalizePick(p, leagueMap))
            .filter((p) => (p?.speciesId || p?.name) && !excludeFoes[(p.speciesId || p.name)?.toLowerCase()]);
    }, [foe1, foe2, foe3, leagueMap, excludeFoes]);

    // results per opponent
    const results = useMemo(() => {
        const out = [];
        for (const enemy of foePicks) {
            if (!enemy?.speciesId && !enemy?.name) continue;
            const r = bestOfThree(myPicks, enemy, myShields, foeShields, null, leagueName);
            out.push({ enemy, ...r });
        }
        return out;
    }, [myPicks, foePicks, myShields, foeShields, leagueName]);

    // simple handlers for text inputs
    const setPick = (setter) => (val) => setter({ ...emptyPick(), speciesId: val, name: val });

    const clearMine = () => {
        setMe1(emptyPick()); setMe2(emptyPick()); setMe3(emptyPick());
        setExcludeMine({});
    };
    const clearFoes = () => {
        setFoe1(emptyPick()); setFoe2(emptyPick()); setFoe3(emptyPick());
        setExcludeFoes({});
    };

    return (
        <div style={{ padding: 20, maxWidth: 1100, margin: "0 auto" }}>
            <h1 style={{ textAlign: "center", marginBottom: 14 }}>Nebsoji PvP Trainer</h1>

            <div style={{ marginBottom: 14 }}>
                <label style={{ fontWeight: 700, marginRight: 8 }}>Select League:</label>
                <select value={leagueName} onChange={(e) => setLeagueName(e.target.value)}>
                    {LEAGUE_NAMES.map((n) => (
                        <option key={n} value={n}>{n}</option>
                    ))}
                </select>
            </div>

            <Row>
                {/* my team */}
                <Card>
                    <div style={{ fontWeight: 800, marginBottom: 6 }}>Your Team (3)</div>

                    <div style={{ marginBottom: 10 }}>
                        <label>PokÃ©mon 1</label>
                        <input
                            placeholder="Zacian (Crowned Sword)"
                            value={me1.name || ""}
                            onChange={(e) => setMe1({ ...me1, name: e.target.value, speciesId: e.target.value })}
                            style={{ width: "100%" }}
                        />
                    </div>
                    <div style={{ marginBottom: 10 }}>
                        <label>PokÃ©mon 2</label>
                        <input
                            placeholder="Groudon"
                            value={me2.name || ""}
                            onChange={(e) => setMe2({ ...me2, name: e.target.value, speciesId: e.target.value })}
                            style={{ width: "100%" }}
                        />
                    </div>
                    <div style={{ marginBottom: 10 }}>
                        <label>PokÃ©mon 3</label>
                        <input
                            placeholder="Dialga (Origin)"
                            value={me3.name || ""}
                            onChange={(e) => setMe3({ ...me3, name: e.target.value, speciesId: e.target.value })}
                            style={{ width: "100%" }}
                        />
                    </div>

                    <ShieldPicker
                        label="Your Shields:"
                        value={myShields}
                        onChange={setMyShields}
                        onReset={() => setMyShields(2)}
                    />

                    <div style={{ marginTop: 10 }}>
                        <button onClick={clearMine}>Reset My Team</button>
                    </div>
                </Card>

                {/* enemies */}
                <Card>
                    <div style={{ fontWeight: 800, marginBottom: 6 }}>Opponents (up to 3)</div>

                    <div style={{ marginBottom: 10 }}>
                        <label>Opponent 1</label>
                        <input
                            placeholder="Ho-Oh"
                            value={foe1.name || ""}
                            onChange={(e) => setFoe1({ ...foe1, name: e.target.value, speciesId: e.target.value })}
                            style={{ width: "100%" }}
                        />
                    </div>
                    <div style={{ marginBottom: 10 }}>
                        <label>Opponent 2</label>
                        <input
                            placeholder="Palkia (Origin)"
                            value={foe2.name || ""}
                            onChange={(e) => setFoe2({ ...foe2, name: e.target.value, speciesId: e.target.value })}
                            style={{ width: "100%" }}
                        />
                    </div>
                    <div style={{ marginBottom: 10 }}>
                        <label>Opponent 3</label>
                        <input
                            placeholder="Rhyperior"
                            value={foe3.name || ""}
                            onChange={(e) => setFoe3({ ...foe3, name: e.target.value, speciesId: e.target.value })}
                            style={{ width: "100%" }}
                        />
                    </div>

                    <ShieldPicker
                        label="Opponent Shields:"
                        value={foeShields}
                        onChange={setFoeShields}
                        onReset={() => setFoeShields(2)}
                    />

                    <div style={{ marginTop: 10 }}>
                        <button onClick={clearFoes}>Reset Enemy</button>
                    </div>
                </Card>
            </Row>

            {/* results */}
            <div style={{ marginTop: 18 }}>
                <div style={{ fontWeight: 800, marginBottom: 8 }}>Live Results</div>
                <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr 1fr", gap: 16 }}>
                    {results.map((block) => (
                        <FightCard
                            key={block.enemy.name || block.enemy.speciesId}
                            titleRight={
                                block.best?.you
                                    ? `Best of your team: ${block.best.you}`
                                    : ""
                            }
                            fight={block.best || null}
                        />
                    ))}
                </div>
            </div>

            <div style={{ fontSize: 11, marginTop: 10, opacity: 0.75 }}>
                Competitive-lite sim (turns, energy, type, STAB, shields). IVs/buffs can be added later - UI stays the same.
            </div>
        </div>
    );
}
