import React, { useEffect, useMemo, useState } from "react";
import PokemonSelect from "./components/PokemonSelect";
import { dedupeBySpecies, toOptions, humanize, indexById } from "./lib/pokeList";
import { LEAGUE_NAMES, importLeague } from "./Data/leagueFiles";
import gm from "./Data/gamemaster.json";
import { buildMoveBook, bestOfThree } from "./battleCalc";

const MOVEBOOK = buildMoveBook(gm);

/* ---------- Small UI bits ---------- */
function ShieldPicker({ label, value, onChange }) {
    const btn = (n) => ({
        padding: "8px 14px",
        border: "1px solid #111",
        borderRadius: 10,
        background: value === n ? "#111" : "#fff",
        color: value === n ? "#fff" : "#111",
        cursor: "pointer",
        marginRight: 8,
        minWidth: 40,
        fontWeight: 700
    });
    return (
        <div style={{ display: "inline-flex", alignItems: "center", gap: 8, marginTop: 8, marginRight: 18 }}>
            <span style={{ minWidth: 120 }}>{label}</span>
            {[0, 1, 2].map((n) => (
                <button key={n} style={btn(n)} onClick={() => onChange(n)}>{n}</button>
            ))}
        </div>
    );
}

function HpBar({ youHP, foeHP }) {
    const wrap = { height: 8, background: "#e5e7eb", borderRadius: 999, overflow: "hidden" };
    const seg = (w, good) => ({
        width: `${Math.max(0, Math.min(100, w))}%`,
        height: "100%",
        background: good ? "#16a34a" : "#dc2626"
    });
    return (
        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 8, marginTop: 6 }}>
            <div>
                <div style={{ fontSize: 12, color: "#6b7280" }}>You: {youHP}</div>
                <div style={wrap}><div style={seg(youHP, true)} /></div>
            </div>
            <div>
                <div style={{ fontSize: 12, color: "#6b7280" }}>Foe: {foeHP}</div>
                <div style={wrap}><div style={seg(foeHP, false)} /></div>
            </div>
        </div>
    );
}

function KillToggle({ dead, onToggle }) {
    const common = {
        border: "1px solid #aaa",
        borderRadius: 8,
        padding: "2px 8px",
        cursor: "pointer",
        fontSize: 12,
        marginLeft: 8
    };
    return (
        <button
            title={dead ? "Mark alive" : "Mark dead"}
            onClick={onToggle}
            style={{ ...common, background: dead ? "#fee2e2" : "#f3f4f6", color: dead ? "#991b1b" : "#111" }}
        >
            {dead ? "☠️ X" : "✖️"}
        </button>
    );
}

/* ---------- App ---------- */
export default function App() {
    const [league, setLeague] = useState(LEAGUE_NAMES[0]);

    const [poolRaw, setPoolRaw] = useState([]);
    const [poolOpts, setPoolOpts] = useState([]);
    const [poolIndex, setPoolIndex] = useState({});

    // YOU: 3 picks (+ dead flags)
    const [me, setMe] = useState([{ id: "", label: "", dead: false }, { id: "", label: "", dead: false }, { id: "", label: "", dead: false }]);
    // OPP: 3 picks (+ dead flags) — only #1 is REQUIRED for calc
    const [op, setOp] = useState([{ id: "", label: "", dead: false }, { id: "", label: "", dead: false }, { id: "", label: "", dead: false }]);

    // Shields (clickable)
    const [myShields, setMyShields] = useState(2);
    const [opShields, setOpShields] = useState(2);

    // Results & errors
    const [results, setResults] = useState(null);
    const [loadError, setLoadError] = useState("");

    // Accept various league JSON shapes
    function normalizeList(modDefault) {
        return Array.isArray(modDefault) ? modDefault
            : Array.isArray(modDefault?.pokemon) ? modDefault.pokemon
                : Array.isArray(modDefault?.data) ? modDefault.data
                    : Array.isArray(modDefault?.list) ? modDefault.list
                        : [];
    }

    // Load league list on league change
    useEffect(() => {
        setLoadError("");
        setResults(null);
        setMe([{ id: "", label: "", dead: false }, { id: "", label: "", dead: false }, { id: "", label: "", dead: false }]);
        setOp([{ id: "", label: "", dead: false }, { id: "", label: "", dead: false }, { id: "", label: "", dead: false }]);

        importLeague(league)
            .then(mod => {
                const list = normalizeList(mod.default);
                const clean = dedupeBySpecies(list);
                setPoolRaw(clean);
                setPoolOpts(toOptions(clean).map(o => o.label));
                setPoolIndex(indexById(clean));
            })
            .catch(err => setLoadError(String(err)));
    }, [league]);

    // map typed label → selection object
    function selectFromLabel(current, setFn, i, typedLabel) {
        const byLabel = poolRaw.find((x) => humanize(x.speciesId) === typedLabel);
        const byId = poolRaw.find((x) => x.speciesId === typedLabel);
        const picked = byLabel || byId;
        const next = [...current];
        next[i] = picked
            ? { id: picked.speciesId, label: humanize(picked.speciesId), dead: false }
            : { id: "", label: typedLabel, dead: false };
        setFn(next);
    }

    // toggle dead/alive
    function toggleDead(which, index) {
        const set = which === "me" ? setMe : setOp;
        const arr = which === "me" ? me : op;
        const next = [...arr];
        next[index] = { ...next[index], dead: !next[index].dead };
        set(next);
    }

    // READY = all 3 of yours chosen (and alive) AND enemy #1 chosen (and alive)
    const ready = useMemo(() => {
        const myAliveChosen = me.filter((m) => !m.dead).every((m) => !!m.id) && me.some((m) => !m.dead);
        const foe1Ready = !!op[0]?.id && !op[0]?.dead;
        return myAliveChosen && foe1Ready;
    }, [me, op]);

    // INSTANT update: recompute any time picks/shields/dead flags change
    useEffect(() => {
        if (!ready) { setResults(null); return; }

        const map = poolIndex;
        const myTeamAlive = me.filter((m) => !m.dead).map((s) => ({ ...map[s.id], name: s.label }));
        const enemy1 = { ...map[op[0].id], name: op[0].label };

        if (!myTeamAlive.length || !enemy1.name) { setResults(null); return; }

        const { best, fights } = bestOfThree(myTeamAlive, enemy1, myShields ?? 2, opShields ?? 2, MOVEBOOK);

        setResults({
            enemy: enemy1.name,
            bestPick: best.you,
            yourRecommended: best.aRecommended,
            fights
        });
    }, [ready, me, op, myShields, opShields, poolIndex]);

    // Reset enemy selections & results
    function resetEnemy() {
        setOp([{ id: "", label: "", dead: false }, { id: "", label: "", dead: false }, { id: "", label: "", dead: false }]);
        setResults(null);
    }

    /* ----------- styles ----------- */
    const h1 = { textAlign: "center", fontSize: 34, fontWeight: 800, margin: "28px 0" };
    const box = { border: "1px solid #e5e7eb", borderRadius: 12, padding: 16, marginBottom: 18, background: "#fff" };
    const colWrap = { display: "grid", gridTemplateColumns: "1fr 1fr", gap: 24, marginTop: 12 };
    const small = { fontSize: 13, color: "#6b7280" };
    const deadStyle = { textDecoration: "line-through", opacity: 0.6 };

    return (
        <div style={{ maxWidth: 980, margin: "0 auto", padding: "20px 16px", fontFamily: "system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif" }}>
            <h1 style={h1}>Nebsoji PvP Trainer</h1>

            {/* League */}
            <div style={box}>
                <label style={{ fontWeight: 600, marginRight: 10 }}>Select League:</label>
                <select value={league} onChange={(e) => setLeague(e.target.value)}>
                    {LEAGUE_NAMES.map((l) => (
                        <option key={l} value={l}>{l}</option>
                    ))}
                </select>
                {loadError && <div style={{ color: "#b91c1c", marginTop: 8, fontSize: 13 }}>Failed to load league data: {loadError}</div>}
            </div>

            {/* Pickers */}
            <div style={colWrap}>
                {/* You: 3 */}
                <div style={box}>
                    <h3 style={{ margin: "0 0 12px 0" }}>Your Team (3)</h3>
                    {me.map((p, i) => (
                        <div key={`me-${i}`} style={{ display: "flex", alignItems: "center" }}>
                            <div style={{ flex: 1 }}>
                                <PokemonSelect
                                    id={`me-${i}`}
                                    label={`Pok\u00E9mon ${i + 1}`}
                                    options={poolOpts}
                                    value={p.label}
                                    onChange={(val) => selectFromLabel(me, setMe, i, val)}
                                    placeholder="Start typing…"
                                />
                            </div>
                            <KillToggle dead={p.dead} onToggle={() => toggleDead("me", i)} />
                        </div>
                    ))}

                    {/* Shields under team selectors */}
                    <div style={{ marginTop: 8 }}>
                        <ShieldPicker label="Your Shields:" value={myShields} onChange={setMyShields} />
                    </div>
                </div>

                {/* Opponents: 3 (only #1 required for calc) */}
                <div style={box}>
                    <div style={{ display: "flex", justifyContent: "space-between", alignItems: "baseline" }}>
                        <h3 style={{ margin: "0 0 12px 0" }}>Opponents (enter #1 now, #2/#3 later)</h3>
                        <button
                            onClick={resetEnemy}
                            style={{ border: "1px solid #111", background: "#fff", borderRadius: 10, padding: "6px 10px", cursor: "pointer" }}
                            title="Clear enemies & results"
                        >
                            Reset Enemy
                        </button>
                    </div>

                    {op.map((p, i) => (
                        <div key={`op-${i}`} style={{ display: "flex", alignItems: "center" }}>
                            <div style={{ flex: 1 }}>
                                <PokemonSelect
                                    id={`op-${i}`}
                                    label={`Opponent ${i + 1}${i === 0 ? " (required)" : ""}`}
                                    options={poolOpts}
                                    value={p.label}
                                    onChange={(val) => selectFromLabel(op, setOp, i, val)}
                                    placeholder="Start typing…"
                                />
                            </div>
                            <KillToggle dead={p.dead} onToggle={() => toggleDead("op", i)} />
                        </div>
                    ))}

                    {/* Opponent shields under enemy selectors */}
                    <div style={{ marginTop: 8 }}>
                        <ShieldPicker label="Opponent Shields:" value={opShields} onChange={setOpShields} />
                    </div>

                    <div style={small}>Live result uses Opponent 1 (if alive). Mark a pick “☠️ X” to exclude it.</div>
                </div>
            </div>

            {/* Results (for enemy #1) */}
            {results && (
                <div style={{ ...box, marginTop: 18 }}>
                    <h3 style={{ marginTop: 0 }}>
                        Live Result vs{" "}
                        <span style={op[0]?.dead ? deadStyle : undefined}><b>{results.enemy}</b></span>
                    </h3>

                    <div style={{
                        display: "grid",
                        gridTemplateColumns: "1fr 1fr 1fr",
                        gap: 12
                    }}>
                        {results.fights.map((o, i) => {
                            const win = o.winner === o.you;
                            const draw = o.winner === "Draw";
                            const badge = {
                                display: "inline-block",
                                padding: "3px 8px",
                                borderRadius: 999,
                                fontSize: 12,
                                fontWeight: 700,
                                color: "#fff",
                                background: draw ? "#6b7280" : win ? "#16a34a" : "#dc2626"
                            };
                            return (
                                <div key={i} style={{ border: "1px solid #e5e7eb", borderRadius: 12, padding: 12 }}>
                                    <div style={{ fontWeight: 800, marginBottom: 4 }}>
                                        <span style={me[i]?.dead ? deadStyle : undefined}>{o.you}</span>
                                    </div>
                                    <div style={{ marginBottom: 6 }}>
                                        <span style={badge}>{draw ? "Draw" : win ? "Win" : "Loss"}</span>
                                        <span style={{ marginLeft: 8, fontSize: 12, color: "#6b7280" }}>vs {results.enemy}</span>
                                    </div>
                                    <HpBar youHP={o.aHP} foeHP={o.bHP} />
                                    <div style={{ marginTop: 8, fontSize: 13, color: "#374151" }}>
                                        Your recommended charged: <b>{o.aRecommended || "—"}</b>
                                    </div>
                                </div>
                            );
                        })}
                    </div>

                    <div style={{ fontSize: 12, color: "#6b7280", marginTop: 10 }}>
                        Quick sim (power/energy only). Types/STAB/CP timing can be added later without changing this UI.
                    </div>
                </div>
            )}
        </div>
    );
}
