// ---------- Damage & CP ----------
function damage(pwr, atk, def, stab, effm) {
    const val = 0.5 * num(pwr, 0) * (num(atk, 1) / Math.max(1, num(def, 1))) * num(stab, 1) * num(effm, 1);
    const out = Math.max(1, Math.floor(val) + 1);
    return Number.isFinite(out) ? out : 1;
}
function cp(atk, def, sta, cpm) {
    return Math.floor((atk * Math.sqrt(def) * Math.sqrt(sta) * cpm * cpm) / 10);
}
function levelForCap(baseAtk, baseDef, baseSta, cap) {
    if (!isFinite(cap)) return 99; // index 99 ≈ level 50
    let best = 99;
    for (let i = 99; i >= 1; i--) {
        const cpm = CPM[i];
        const cAtk = baseAtk * cpm, cDef = baseDef * cpm, cSta = baseSta * cpm;
        if (cp(cAtk, cDef, cSta, 1) <= cap) { best = i; break; }
    }
    return best;
}
function eff(moveType, defendTypes) {
    let m = 1;
    for (const t of defendTypes) { m *= (EFF[moveType]?.[t] ?? 1); }
    return m;
}

// ---------- Books ----------
function buildSpeciesBook(gm) {
    const poke = gm?.pokemon || gm?.data?.pokemon || gm?.species || [];
    const out = {};
    for (const p of poke) {
        const id = normId(p.speciesId || p.pokemonId || p.id || p.name || p.templateId);
        if (!id) continue;
        const types = [normType(p.type1 || p.types?.[0]), normType(p.type2 || p.types?.[1])].filter(Boolean);
        const atk = num(p.baseAttack ?? p.stats?.atk ?? p.attack, 200);
        const def = num(p.baseDefense ?? p.stats?.def ?? p.defense, 200);
        const sta = num(p.baseStamina ?? p.stats?.sta ?? p.stamina, 200);
        out[id] = { atk, def, sta, types: types.length ? types : ["Normal"] };
    }
    return out;
}

export function buildMoveBook(gm) {
    SPECIES = buildSpeciesBook(gm);

    const pools = [
        gm?.moves, gm?.combatMoves,
        gm?.data?.moves, gm?.data?.combatMoves,
        gm?.moveList
    ].filter(Boolean);

    const out = {};

    for (const list of pools) {
        for (const m of list) {
            const rawId = m.moveId ?? m.id ?? m.uniqueId ?? m.templateId ?? m.name ?? "";
            const id = canonMoveId(rawId);
            if (!id) continue;

            const type = normType(m.type || m.pokemonType || m.moveType) || "Normal";

            // Accept any of the usual power fields
            const power = num(m.pvpPower ?? m.power ?? m.combatPower ?? m.damage, 0);

            // Decide fast vs charged; accept any schema; DO NOT allow undefined numbers
            let kind = "fast";
            let energyGain = 0;
            let energyCost = 0;
            let turns = 0;

            // Niantic: energyDelta (>0 fast, <0 charged); durationTurns for PvP
            if (m.energyDelta != null) {
                const ed = num(m.energyDelta, 0);
                if (ed > 0) { kind = "fast"; energyGain = ed; }
                if (ed < 0) { kind = "charged"; energyCost = Math.abs(ed); }
            }
            // PvPoke: energyGain for fast; energy for charged
            if (m.energyGain != null) { kind = "fast"; energyGain = num(m.energyGain, 0); }
            if (m.energy != null) { kind = "charged"; energyCost = Math.abs(num(m.energy, 0)); }

            // Turns: prefer explicit; otherwise default 1 for fast
            turns = num(m.durationTurns ?? m.turns ?? m.cooldownTurns, kind === "fast" ? 1 : 0);
            if (kind === "fast") { turns = Math.max(1, Math.floor(turns) || 1); }

            // ---------- SAFE DEFAULTS so battles always progress ----------
            // If a fast move has no energy (common in some GM dumps), assume a reasonable default.
            // This ensures charged moves and shields still occur.
            if (kind === "fast" && energyGain <= 0) {
                // Mildly generous but sane default: 8 energy per 1 turn (so ~45 energy in ~6 "taps")
                console.warn(`[PvP] fast move ${id} missing energyGain; using fallback 8.`);
                energyGain = 8;
            }
            // If a charged move has no cost, assume 45
            if (kind === "charged" && energyCost <= 0) {
                console.warn(`[PvP] charged move ${id} missing energy cost; using fallback 45.`);
                energyCost = 45;
            }

            const move = { id, kind, type, power, energyGain, energyCost, turns };
            // keep highest-power duplicate variant
            if (!out[id] || power > out[id].power) out[id] = move;
        }
    }

    MOVES = out;
    return out;
}


// ---------- Choose charged by expected damage vs defender ----------
function bestCharged(att, def) {
    let best = null, bestScore = -Infinity;
    for (const id of (att.chargedMoves || [])) {
        const m = MOVES[id];
        if (!m || m.kind !== "charged") continue;
        const stab = att.types.includes(m.type) ? STAB : 1;
        const effm = eff(m.type, def.types);
        const dmg = damage(m.power, att.Atk, def.Def, stab, effm);
        const dpe = (m.power || 0) / (m.energyCost || 45);
        const score = dmg * 1.0 + dpe * 2.5; // damage first, efficiency second
        if (score > bestScore) { bestScore = score; best = m; }
    }
    return best;
}

// ---------- Duel simulation ----------
export function simulateDuel(attackerIn, defenderIn, shieldsA = 2, shieldsB = 2, _book = MOVES, leagueName = "Master League") {
    const cap =
        typeof leagueName === "string" ? (LEAGUE_CAP[leagueName] ?? Infinity)
            : (Number.isFinite(leagueName) ? Number(leagueName) : Infinity);

    function enrichSide(src) {
        const id = normId(src.speciesId || src.name);
        const base = SPECIES[id] || { atk: 200, def: 200, sta: 200, types: ["Normal"] };
        const lvlIndex = levelForCap(base.atk, base.def, base.sta, cap);
        const cpm = CPM[lvlIndex] || CPM[99];

        const Atk = num(base.atk * cpm, 200);
        const Def = num(base.def * cpm, 200);
        const Sta = Math.max(1, Math.floor(num(base.sta * cpm, 200)) * 2); // tankier bars

        const fastId = canonMoveId(src.fastMove);
        const fast = MOVES[fastId] || null;

        const charged = (src.chargedMoves || [])
            .map(canonMoveId)
            .map(id => MOVES[id])
            .filter(Boolean);

        return {
            name: src.name || src.speciesId,
            speciesId: id,
            types: base.types,
            Atk, Def, MaxHP: Sta, HP: Sta,
            energy: 0,
            fast, chargedMoves: charged,
            rec: null
        };
    }

    const A = enrichSide(attackerIn);
    const B = enrichSide(defenderIn);

    A.rec = bestCharged(A, B);
    B.rec = bestCharged(B, A);

    const MAX_TURNS = 2000;
    let aSh = Math.max(0, shieldsA | 0), bSh = Math.max(0, shieldsB | 0);
    let t = 0;
    const log = [];

    const canThrow = (u) => (u.chargedMoves || []).some(m => u.energy >= (m.energyCost || 45));
    const chooseThrow = (u, foe) => {
        let pick = null, best = -Infinity;
        for (const m of (u.chargedMoves || [])) {
            if (u.energy < (m.energyCost || 45)) continue;
            const stab = u.types.includes(m.type) ? STAB : 1;
            const mult = eff(m.type, foe.types);
            const dmg = damage(m.power, u.Atk, foe.Def, stab, mult);
            if (dmg > best) { best = dmg; pick = m; }
        }
        return pick;
    };

    while (A.HP > 0 && B.HP > 0 && t < MAX_TURNS) {
        t++;

        // fast energy gain by turns
        if (A.fast && t % (A.fast.turns || 1) === 0) A.energy += num(A.fast.energyGain, 0);
        if (B.fast && t % (B.fast.turns || 1) === 0) B.energy += num(B.fast.energyGain, 0);

        const aMove = canThrow(A) ? chooseThrow(A, B) : null;
        const bMove = canThrow(B) ? chooseThrow(B, A) : null;

        const aFirst = aMove && bMove ? (A.Atk >= B.Atk) : !!aMove;

        function resolveThrow(user, foe, move, who) {
            if (!move) return;
            const stab = user.types.includes(move.type) ? STAB : 1;
            const mult = eff(move.type, foe.types);
            const hit = damage(move.power, user.Atk, foe.Def, stab, mult);

            let shielded = false;
            if (who === "A" && bSh > 0) {
                if (hit >= foe.HP || hit >= foe.MaxHP * 0.35) { bSh--; shielded = true; }
            } else if (who === "B" && aSh > 0) {
                if (hit >= foe.HP || hit >= foe.MaxHP * 0.35) { aSh--; shielded = true; }
            }

            if (!shielded) { foe.HP = Math.max(0, foe.HP - hit); }
            user.energy -= (move.energyCost || 45);
            log.push(`${who} throws ${move.id}${shielded ? ' (shielded)' : ''}`);
        }

        if (aFirst) {
            if (aMove) resolveThrow(A, B, aMove, "A");
            else if (A.fast && t % (A.fast.turns || 1) === 0) {
                const hit = damage(A.fast.power || 1, A.Atk, B.Def, A.types.includes(A.fast.type) ? STAB : 1, eff(A.fast.type, B.types));
                B.HP = Math.max(0, B.HP - hit);
            }
            if (B.HP <= 0) break;

            if (bMove) resolveThrow(B, A, bMove, "B");
            else if (B.fast && t % (B.fast.turns || 1) === 0) {
                const hit = damage(B.fast.power || 1, B.Atk, A.Def, B.types.includes(B.fast.type) ? STAB : 1, eff(B.fast.type, A.types));
                A.HP = Math.max(0, A.HP - hit);
            }
            if (A.HP <= 0) break;

        } else {
            if (bMove) resolveThrow(B, A, bMove, "B");
            else if (B.fast && t % (B.fast.turns || 1) === 0) {
                const hit = damage(B.fast.power || 1, B.Atk, A.Def, B.types.includes(B.fast.type) ? STAB : 1, eff(B.fast.type, A.types));
                A.HP = Math.max(0, A.HP - hit);
            }
            if (A.HP <= 0) break;

            if (aMove) resolveThrow(A, B, aMove, "A");
            else if (A.fast && t % (A.fast.turns || 1) === 0) {
                const hit = damage(A.fast.power || 1, A.Atk, B.Def, A.types.includes(A.fast.type) ? STAB : 1, eff(A.fast.type, B.types));
                B.HP = Math.max(0, B.HP - hit);
            }
            if (B.HP <= 0) break;
        }
    }

    const winner =
        A.HP <= 0 && B.HP <= 0 ? "Draw" :
            A.HP <= 0 ? B.name :
                B.HP <= 0 ? A.name :
                    (A.HP === B.HP ? "Draw" : (A.HP > B.HP ? A.name : B.name));

    return {
        winner,
        aHP: Math.round((A.HP / A.MaxHP) * 100),
        bHP: Math.round((B.HP / B.MaxHP) * 100),
        aRecommended: A.rec?.id || null,
        bRecommended: B.rec?.id || null,
        summary: log.slice(0, 12)
    };
}

// Rank your 3 vs one enemy (returns shape your App expects)
export function bestOfThree(mine, enemy, myShields, foeShields, _book = MOVES, leagueName = "Master League") {
    const fights = mine.map(m => {
        const you = { ...m, name: m.name || m.speciesId };
        const foe = { ...enemy, name: enemy.name || enemy.speciesId };
        const r = simulateDuel(you, foe, myShields, foeShields, MOVES, leagueName);
        const score = r.winner === you.name ? 1 : (r.winner === "Draw" ? 0 : -1);
        return { you: you.name, vs: foe.name, ...r, score };
    });
    fights.sort((a, b) => (b.score - a.score) || (b.aHP - a.aHP) || (b.bHP - a.bHP));
    return { best: fights[0], fights };
}
