    const A = makeBattler(p1In);
    const B = makeBattler(p2In);

    const MAX_TURNS = 1000; // 500s
    for (let t = 1; t <= MAX_TURNS; t++) {
        if (A.hp <= 0 || B.hp <= 0) break;

        // Determine actions
        const aCharged = pickReadyCharged(A, B);
        const bCharged = pickReadyCharged(B, A);

        // Resolve charged first; if both, CMP via true Atk (break ties by speciesId)
        if (aCharged || bCharged) {
            const first = (aCharged && bCharged)
                ? (A.atk === B.atk ? (A.speciesId < B.speciesId ? "A" : "B") : (A.atk > B.atk ? "A" : "B"))
                : (aCharged ? "A" : "B");

            if (first === "A") { resolveCharged(A, B, aCharged); if (B.hp <= 0) break; if (bCharged) resolveCharged(B, A, bCharged); }
            else { resolveCharged(B, A, bCharged); if (A.hp <= 0) break; if (aCharged) resolveCharged(A, B, aCharged); }

            // Using a charged consumes your turn; reset fast windows
            if (aCharged) A.fastTurnsLeft = A.fast.turns || 1;
            if (bCharged) B.fastTurnsLeft = B.fast.turns || 1;
            continue;
        }

        // Otherwise progress fast moves simultaneously
        stepFast(A, B);
        if (B.hp <= 0) break;
        stepFast(B, A);
        if (A.hp <= 0) break;
    }

    const result =
        (A.hp <= 0 && B.hp <= 0) ? "draw" :
            (B.hp <= 0) ? "p1" :
                (A.hp <= 0) ? "p2" : "timeout";

    return {
        result,
        p1: { hp: A.hpMax ? Math.round((A.hp / A.hpMax) * 100) : 0, shields: A.shields, energy: A.energy },
        p2: { hp: B.hpMax ? Math.round((B.hp / B.hpMax) * 100) : 0, shields: B.shields, energy: B.energy }
    };
}

function pickReadyCharged(me, opp) {
    // Choose highest damage charged that is ready; else null
    const ready = me.charged.filter(m => me.energy >= (m.energy || 999));
    if (!ready.length) return null;
    let best = ready[0], bestVal = -1;
    for (const m of ready) {
        const dmg = projDamage(me, opp, m);
        const score = dmg + (100 - (m.energy || 100)) * 0.05;
        if (score > bestVal) { bestVal = score; best = m; }
    }
    return best;
}

function resolveCharged(att, def, move) {
    if (!move) return;
    if (def.shields > 0) {
        def.shields -= 1;                // shield consumes, no damage
    } else {
        def.hp = Math.max(0, def.hp - projDamage(att, def, move));
    }
    att.energy = clamp(att.energy - (move.energy || 0), 0, 100);
}

function stepFast(att, def) {
    att.fastTurnsLeft -= 1;
    if (att.fastTurnsLeft <= 0) {
        // Fast move lands now
        def.hp = Math.max(0, def.hp - projDamage(att, def, att.fast));
        att.energy = clamp(att.energy + (att.fast.energyGain || 0), 0, 100);
        att.fastTurnsLeft = att.fast.turns || 1;
    }
}

function projDamage(att, def, move) {
    if (!move) return 0;
    const stab = att.types.includes(move.type) ? STAB : 1;
    const eff = typeMult(move.type, def.types);
    return pvpDamage(move.power || 0, att.atk, def.def, stab, eff);
}
