import React, { useEffect, useMemo, useState } from "react";
import PokemonSelect from "./components/PokemonSelect";
import { dedupeBySpecies, toOptions, humanize, indexById } from "./lib/pokeList";
import { LEAGUE_NAMES, importLeague } from "./Data/leagueFiles";
import gm from "./Data/gamemaster.json";
import { buildMoveBook, bestOfThree, simulateDuel } from "./battleCalc";

const MOVEBOOK = buildMoveBook(gm);

/* ---------- Small UI bits ---------- */
function ShieldPicker({ label, value, onChange }) {
    const btn = (n) => ({
        padding: "8px 14px",
        border: "1px solid #111",
        borderRadius: 10,
        background: value === n ? "#111" : "#fff",
        color: value === n ? "#fff" : "#111",
        cursor: "pointer",
        marginRight: 8,
        minWidth: 40,
        fontWeight: 700
    });
    return (
        <div style={{ display: "inline-flex", alignItems: "center", gap: 8, marginTop: 8, marginRight: 18 }}>
            <span style={{ minWidth: 120 }}>{label}</span>
            {[0, 1, 2].map((n) => (
                <button key={n} style={btn(n)} onClick={() => onChange(n)}>{n}</button>
            ))}
        </div>
    );
}

function HpBar({ youHP, foeHP }) {
    const wrap = { height: 8, background: "#e5e7eb", borderRadius: 999, overflow: "hidden" };
    const seg = (w, good) => ({
        width: `${Math.max(0, Math.min(100, w))}%`,
        height: "100%",
        background: good ? "#16a34a" : "#dc2626"
    });
    return (
        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 8, marginTop: 6 }}>
            <div>
                <div style={{ fontSize: 12, color: "#6b7280" }}>You: {youHP}</div>
                <div style={wrap}><div style={seg(youHP, true)} /></div>
            </div>
            <div>
                <div style={{ fontSize: 12, color: "#6b7280" }}>Foe: {foeHP}</div>
                <div style={wrap}><div style={seg(foeHP, false)} /></div>
            </div>
        </div>
    );
}

function KillToggle({ dead, onToggle }) {
    const common = {
        border: "1px solid #aaa",
        borderRadius: 8,
        padding: "2px 8px",
        cursor: "pointer",
        fontSize: 12,
        marginLeft: 8
    };
    return (
        <button
            title={dead ? "Mark alive" : "Mark dead"}
            onClick={onToggle}
            style={{ ...common, background: dead ? "#fee2e2" : "#f3f4f6", color: dead ? "#991b1b" : "#111" }}
        >
            {dead ? "☠️ X" : "✖️"}
        </button>
    );
}

/* ---------- Helpers for advice ---------- */
function outcomeLabel(winner, youName) {
    return winner === youName ? "Win" : (winner === "Draw" ? "Draw" : "Loss");
}
function scoreFromOutcome(label) {
    return label === "Win" ? 1 : (label === "Draw" ? 0 : -1);
}
// What-if shield advice for the BEST pick in a matchup
function shieldAdvice({ mine, enemy, myShields, opShields, book }) {
    const cur = simulateDuel(mine, enemy, myShields, opShields, book);
    const curLabel = outcomeLabel(cur.winner, mine.name);

    const less = simulateDuel(mine, enemy, Math.max(0, myShields - 1), opShields, book);
    const lessLabel = outcomeLabel(less.winner, mine.name);

    const more = simulateDuel(mine, enemy, Math.min(2, myShields + 1), opShields, book);
    const moreLabel = outcomeLabel(more.winner, mine.name);

    let tip = `With ${myShields} shield${myShields !== 1 ? "s" : ""}: ${curLabel}.`;
    if (scoreFromOutcome(moreLabel) > scoreFromOutcome(curLabel)) {
        tip += ` If you can hold +1, it becomes ${moreLabel}.`;
    }
    if (scoreFromOutcome(lessLabel) < scoreFromOutcome(curLabel)) {
        tip += ` If you drop -1, it becomes ${lessLabel}.`;
    }
    return {
        text: tip,
        current: curLabel,
        enemyThreat: cur.bRecommended || null // enemy’s scariest charged (by DPE) from the sim
    };
}

/* ---------- App ---------- */
export default function App() {
    const [league, setLeague] = useState(LEAGUE_NAMES[0]);

    const [poolRaw, setPoolRaw] = useState([]);
    const [poolOpts, setPoolOpts] = useState([]);
    const [poolIndex, setPoolIndex] = useState({});

    // YOU: 3 picks (+ dead flags)
    const [me, setMe] = useState([
        { id: "", label: "", dead: false },
        { id: "", label: "", dead: false },
        { id: "", label: "", dead: false }
    ]);
    // OPP: 3 picks (+ dead flags) — each alive enemy triggers its own calc
    const [op, setOp] = useState([
        { id: "", label: "", dead: false },
        { id: "", label: "", dead: false },
        { id: "", label: "", dead: false }
    ]);

    // Shields (clickable)
    const [myShields, setMyShields] = useState(2);
    const [opShields, setOpShields] = useState(2);

    // Results & errors
    const [results, setResults] = useState(null);
    const [loadError, setLoadError] = useState("");

    // Accept various league JSON shapes
    function normalizeList(modDefault) {
        return Array.isArray(modDefault) ? modDefault
            : Array.isArray(modDefault?.pokemon) ? modDefault.pokemon
                : Array.isArray(modDefault?.data) ? modDefault.data
                    : Array.isArray(modDefault?.list) ? modDefault.list
                        : [];
    }

    // Load league list on league change
    useEffect(() => {
        setLoadError("");
        setResults(null);
        setMe([
            { id: "", label: "", dead: false },
            { id: "", label: "", dead: false },
            { id: "", label: "", dead: false }
        ]);
        setOp([
            { id: "", label: "", dead: false },
            { id: "", label: "", dead: false },
            { id: "", label: "", dead: false }
        ]);

        importLeague(league)
            .then(mod => {
                const list = normalizeList(mod.default);
                const clean = dedupeBySpecies(list);
                setPoolRaw(clean);
                setPoolOpts(toOptions(clean).map(o => o.label));
                setPoolIndex(indexById(clean));
            })
            .catch(err => setLoadError(String(err)));
    }, [league]);

    // map typed label → selection object
    function selectFromLabel(current, setFn, i, typedLabel) {
        const byLabel = poolRaw.find((x) => humanize(x.speciesId) === typedLabel);
        const byId = poolRaw.find((x) => x.speciesId === typedLabel);
        const picked = byLabel || byId;
        const next = [...current];
        next[i] = picked
            ? { id: picked.speciesId, label: humanize(picked.speciesId), dead: false }
            : { id: "", label: typedLabel, dead: false };
        setFn(next);
    }

    // toggle dead/alive
    function toggleDead(which, index) {
        const set = which === "me" ? setMe : setOp;
        const arr = which === "me" ? me : op;
        const next = [...arr];
        next[index] = { ...next[index], dead: !next[index].dead };
        set(next);
    }

    // READY if: at least one of your mons is alive+picked, and at least one enemy is alive+picked.
    const ready = useMemo(() => {
        const myAliveChosen = me.some((m) => !!m.id && !m.dead);
        const foeAliveChosen = op.some((o) => !!o.id && !o.dead);
        return myAliveChosen && foeAliveChosen;
    }, [me, op]);

    // INSTANT update: recompute for EVERY alive enemy any time picks/shields/dead flags change
    useEffect(() => {
        if (!ready) { setResults(null); return; }

        const map = poolIndex;
        const myTeamAlive = me.filter((m) => !m.dead && m.id).map((s) => ({ ...map[s.id], name: s.label }));
        const enemiesAlive = op
            .map((s) => (s.id && !s.dead ? { ...map[s.id], name: s.label } : null))
            .filter(Boolean);

        if (!myTeamAlive.length || !enemiesAlive.length) { setResults(null); return; }

        const perEnemy = enemiesAlive.map((enemy) => {
            const { best, fights } = bestOfThree(myTeamAlive, enemy, myShields ?? 2, opShields ?? 2, MOVEBOOK);

            // Shield advice for the BEST pick only
            const bestMine = myTeamAlive.find((m) => m.name === best.you) || myTeamAlive[0];
            const advice = shieldAdvice({
                mine: bestMine,
                enemy,
                myShields: myShields ?? 2,
                opShields: opShields ?? 2,
                book: MOVEBOOK
            });

            return {
                enemy: enemy.name,
                bestPick: best.you,
                yourRecommended: best.aRecommended,
                enemyThreat: advice.enemyThreat,  // move to watch for
                shieldAdvice: advice.text,
                fights
            };
        });

        setResults({ perEnemy });
    }, [ready, me, op, myShields, opShields, poolIndex]);

    // Reset enemy selections & results
    function resetEnemy() {
        setOp([
            { id: "", label: "", dead: false },
            { id: "", label: "", dead: false },
            { id: "", label: "", dead: false }
        ]);
        setResults(null);
    }

    /* ----------- styles ----------- */
    const h1 = { textAlign: "center", fontSize: 34, fontWeight: 800, margin: "28px 0" };
    const box = { border: "1px solid #e5e7eb", borderRadius: 12, padding: 16, marginBottom: 18, background: "#fff" };
    const colWrap = { display: "grid", gridTemplateColumns: "1fr 1fr", gap: 24, marginTop: 12 };
    const small = { fontSize: 13, color: "#6b7280" };

    // --- NEW: results row layout (cards go ACROSS) ---
    const resultsGrid = {
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(320px, 1fr))",
        gap: 12,
        alignItems: "start"
    };

    return (
        <div style={{ maxWidth: 1200, margin: "0 auto", padding: "20px 16px", fontFamily: "system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif" }}>
            <h1 style={h1}>Nebsoji PvP Trainer</h1>

            {/* League */}
            <div style={box}>
                <label style={{ fontWeight: 600, marginRight: 10 }}>Select League:</label>
                <select value={league} onChange={(e) => setLeague(e.target.value)}>
                    {LEAGUE_NAMES.map((l) => (
                        <option key={l} value={l}>{l}</option>
                    ))}
                </select>
                {loadError && <div style={{ color: "#b91c1c", marginTop: 8, fontSize: 13 }}>Failed to load league data: {loadError}</div>}
            </div>

            {/* Pickers */}
            <div style={colWrap}>
                {/* You: 3 */}
                <div style={box}>
                    <h3 style={{ margin: "0 0 12px 0" }}>Your Team (3)</h3>
                    {me.map((p, i) => (
                        <div key={`me-${i}`} style={{ display: "flex", alignItems: "center" }}>
                            <div style={{ flex: 1 }}>
                                <PokemonSelect
                                    id={`me-${i}`}
                                    label={`Pok\u00E9mon ${i + 1}`}
                                    options={poolOpts}
                                    value={p.label}
                                    onChange={(val) => selectFromLabel(me, setMe, i, val)}
                                    placeholder="Start typing…"
                                />
                            </div>
                            <KillToggle dead={p.dead} onToggle={() => toggleDead("me", i)} />
                        </div>
                    ))}
                    {/* Shields under team selectors */}
                    <div style={{ marginTop: 8 }}>
                        <ShieldPicker label="Your Shields:" value={myShields} onChange={setMyShields} />
                    </div>
                </div>

                {/* Opponents: 3 (each alive/selected enemy gets its own live result) */}
                <div style={box}>
                    <div style={{ display: "flex", justifyContent: "space-between", alignItems: "baseline" }}>
                        <h3 style={{ margin: "0 0 12px 0" }}>Opponents (up to 3)</h3>
                        <button
                            onClick={resetEnemy}
                            style={{ border: "1px solid #111", background: "#fff", borderRadius: 10, padding: "6px 10px", cursor: "pointer" }}
                            title="Clear enemies & results"
                        >
                            Reset Enemy
                        </button>
                    </div>

                    {op.map((p, i) => (
                        <div key={`op-${i}`} style={{ display: "flex", alignItems: "center" }}>
                            <div style={{ flex: 1 }}>
                                <PokemonSelect
                                    id={`op-${i}`}
                                    label={`Opponent ${i + 1}`}
                                    options={poolOpts}
                                    value={p.label}
                                    onChange={(val) => selectFromLabel(op, setOp, i, val)}
                                    placeholder="Start typing…"
                                />
                            </div>
                            <KillToggle dead={p.dead} onToggle={() => toggleDead("op", i)} />
                        </div>
                    ))}

                    {/* Opponent shields under selectors */}
                    <div style={{ marginTop: 8 }}>
                        <ShieldPicker label="Opponent Shields:" value={opShields} onChange={setOpShields} />
                    </div>

                    <div style={small}>Mark a pick “☠️ X” to exclude it from the calc.</div>
                </div>
            </div>

            {/* Results: cards ACROSS the page */}
            {results && results.perEnemy?.length > 0 && (
                <div style={{ ...box, marginTop: 18 }}>
                    <h3 style={{ marginTop: 0 }}>Live Results</h3>

                    <div style={resultsGrid}>
                        {results.perEnemy.map((E, i) => (
                            <div key={i} style={{ border: "1px solid #e5e7eb", borderRadius: 12, padding: 12 }}>
                                <div style={{ display: "flex", alignItems: "baseline", justifyContent: "space-between" }}>
                                    <div style={{ fontWeight: 800 }}>vs <b>{E.enemy}</b></div>
                                    <div style={{ fontSize: 12, background: "#f3f4f6", padding: "2px 8px", borderRadius: 999 }}>
                                        Danger move: <b>{E.enemyThreat || "—"}</b>
                                    </div>
                                </div>

                                <div style={{ fontSize: 13, color: "#374151", marginTop: 6, marginBottom: 8 }}>
                                    Best of your 3: <b>{E.bestPick}</b> &nbsp;|&nbsp; Your charged to throw: <b>{E.yourRecommended || "—"}</b>
                                </div>

                                {/* three fight strips (your 3 vs this enemy) */}
                                <div style={{ display: "grid", gridTemplateColumns: "1fr", gap: 8 }}>
                                    {E.fights.map((o, j) => {
                                        const win = o.winner === o.you;
                                        const draw = o.winner === "Draw";
                                        const badge = {
                                            display: "inline-block",
                                            padding: "3px 8px",
                                            borderRadius: 999,
                                            fontSize: 12,
                                            fontWeight: 700,
                                            color: "#fff",
                                            background: draw ? "#6b7280" : win ? "#16a34a" : "#dc2626"
                                        };
                                        return (
                                            <div key={j} style={{ border: "1px solid #eef2f7", borderRadius: 10, padding: 10 }}>
                                                <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                                                    <div style={{ fontWeight: 700 }}>{o.you}</div>
                                                    <span style={badge}>{draw ? "Draw" : win ? "Win" : "Loss"}</span>
                                                </div>
                                                <HpBar youHP={o.aHP} foeHP={o.bHP} />
                                                <div style={{ marginTop: 8, fontSize: 13, color: "#374151" }}>
                                                    Your rec. charged: <b>{o.aRecommended || "—"}</b>
                                                </div>
                                            </div>
                                        );
                                    })}
                                </div>

                                {/* Shield advice for the best pick */}
                                <div style={{ fontSize: 13, color: "#111", marginTop: 10, background: "#f8fafc", padding: 8, borderRadius: 8 }}>
                                    <b>Shield advice:</b> {E.shieldAdvice}
                                </div>
                            </div>
                        ))}
                    </div>

                    <div style={{ fontSize: 12, color: "#6b7280", marginTop: 10 }}>
                        Quick sim (power/energy only). We can add types/STAB/CP timing later—UI stays the same.
                    </div>
                </div>
            )}
        </div>
    );
}
