// src/components/PokemonSelect.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";

export default function PokemonSelect({
    id,
    label,
    options = [],              // array of strings (labels)
    // value is intentionally ignored so the input doesn't persist after a pick
    value,                      // eslint-disable-line no-unused-vars
    onChange,                   // called ONLY on selection with the picked label
    placeholder = "Start typing�",
    maxSuggestions = 5,
}) {
    const [term, setTerm] = useState("");     // the text the user is typing
    const [open, setOpen] = useState(false);  // dropdown open state
    const wrapRef = useRef(null);

    // prefix/word-start matcher
    const norm = (s) => String(s || "").toLowerCase();
    const startsAtWord = (label, q) => {
        const L = norm(label);
        const Q = norm(q).trim();
        if (!Q) return false;                    // don't show anything until they type
        if (L.startsWith(Q)) return true;        // start of whole label
        const words = L.split(/[\s\-\(\)_]+/g).filter(Boolean);
        return words.some((w) => w.startsWith(Q));
    };

    const list = useMemo(() => {
        if (!term) return [];
        return options.filter((opt) => startsAtWord(opt, term)).slice(0, maxSuggestions);
    }, [options, term, maxSuggestions]);

    // click-away to close
    useEffect(() => {
        function onDocClick(e) {
            if (!wrapRef.current) return;
            if (!wrapRef.current.contains(e.target)) setOpen(false);
        }
        document.addEventListener("mousedown", onDocClick);
        return () => document.removeEventListener("mousedown", onDocClick);
    }, []);

    function pick(v) {
        // notify parent with the chosen label
        onChange?.(v);
        // clear the input and close � do not persist text
        setTerm("");
        setOpen(false);
    }

    function onKeyDown(e) {
        if (e.key === "Enter" && list.length > 0) {
            e.preventDefault();
            pick(list[0]);
        } else if (e.key === "Escape") {
            setOpen(false);
        }
    }

    return (
        <div ref={wrapRef} style={{ position: "relative", marginBottom: 12 }}>
            {label && (
                <label htmlFor={id} style={{ display: "block", marginBottom: 6 }}>
                    {label}
                </label>
            )}

            <input
                id={id}
                type="text"
                value={term}
                placeholder={placeholder}
                onFocus={() => {
                    // don't open on focus; only when they actually type
                    if (term) setOpen(true);
                }}
                onChange={(e) => {
                    const v = e.target.value;
                    setTerm(v);
                    setOpen(!!v); // open only after they type something
                }}
                onKeyDown={onKeyDown}
                style={{
                    width: "100%",
                    border: "1px solid #d1d5db",
                    borderRadius: 8,
                    padding: "8px 10px",
                    outline: "none",
                }}
            />

            {open && list.length > 0 && (
                <div
                    style={{
                        position: "absolute",
                        zIndex: 20,
                        top: "100%",
                        left: 0,
                        right: 0,
                        background: "#111",           // matches your dark dropdown style
                        color: "#fff",
                        borderRadius: 8,
                        boxShadow: "0 8px 24px rgba(0,0,0,0.18)",
                        marginTop: 6,
                        maxHeight: 220,
                        overflowY: "auto",
                    }}
                >
                    {list.map((opt, i) => (
                        <button
                            key={`${opt}-${i}`}
                            onClick={() => pick(opt)}
                            onMouseDown={(e) => e.preventDefault()} // keep focus
                            style={{
                                display: "block",
                                width: "100%",
                                textAlign: "left",
                                padding: "10px 12px",
                                border: "none",
                                background: "transparent",
                                cursor: "pointer",
                            }}
                        >
                            {opt}
                        </button>
                    ))}
                </div>
            )}
        </div>
    );
}
