// src/components/PokemonSelect.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";

export default function PokemonSelect({
    id,
    label,
    options = [],
    value = "",
    onChange,
    placeholder = "Start typing�",
    maxSuggestions = 8,
}) {
    const [open, setOpen] = useState(false);
    const [term, setTerm] = useState(value || "");
    const [hi, setHi] = useState(-1);
    const wrapRef = useRef(null);

    // keep controlled value in sync if parent updates it
    useEffect(() => {
        setTerm(value || "");
        setHi(-1);
    }, [value]);

    // ---- strict prefix-only matcher (whole label) ----
    const norm = (s) => String(s || "").toLowerCase();
    const matches = (label, q) => {
        const L = norm(label);
        const Q = norm(q).trim();
        return Q.length > 0 && L.startsWith(Q);
    };

    const list = useMemo(() => {
        const q = term.trim();
        if (!q) return []; // no suggestions until the user types
        return options.filter((opt) => matches(opt, q)).slice(0, maxSuggestions);
    }, [options, term, maxSuggestions]);

    // click-away to close
    useEffect(() => {
        function onDocClick(e) {
            if (!wrapRef.current) return;
            if (!wrapRef.current.contains(e.target)) setOpen(false);
        }
        document.addEventListener("mousedown", onDocClick);
        return () => document.removeEventListener("mousedown", onDocClick);
    }, []);

    function pick(v) {
        setTerm(v);
        setOpen(false);
        setHi(-1);
        onChange?.(v);
    }

    function onKeyDown(e) {
        if (!open && (e.key === "ArrowDown" || e.key === "ArrowUp")) {
            setOpen(true);
            return;
        }
        if (e.key === "ArrowDown") {
            e.preventDefault();
            setHi((i) => (list.length ? (i + 1) % list.length : -1));
        } else if (e.key === "ArrowUp") {
            e.preventDefault();
            setHi((i) => (list.length ? (i - 1 + list.length) % list.length : -1));
        } else if (e.key === "Enter") {
            if (open && list.length && hi >= 0) {
                e.preventDefault();
                pick(list[hi]);
            }
        } else if (e.key === "Escape") {
            setOpen(false);
            setHi(-1);
        }
    }

    return (
        <div ref={wrapRef} style={{ position: "relative" }}>
            {label && (
                <label
                    htmlFor={id}
                    style={{ display: "block", fontSize: 13, color: "#374151", marginBottom: 4 }}
                >
                    {label}
                </label>
            )}

            <input
                id={id}
                type="text"
                value={term}
                placeholder={placeholder}
                onFocus={() => setOpen(true)}
                onKeyDown={onKeyDown}
                onChange={(e) => {
                    setTerm(e.target.value);
                    setOpen(true);
                    setHi(-1);
                    // don't call onChange here; only when a suggestion is picked
                }}
                style={{
                    width: "100%",
                    border: "1px solid #d1d5db",
                    borderRadius: 8,
                    padding: "8px 10px",
                    outline: "none",
                }}
                autoComplete="off"
                spellCheck={false}
            />

            {open && list.length > 0 && (
                <div
                    role="listbox"
                    style={{
                        position: "absolute",
                        zIndex: 20,
                        top: "100%",
                        left: 0,
                        right: 0,
                        background: "#fff",
                        border: "1px solid #e5e7eb",
                        borderRadius: 8,
                        boxShadow: "0 8px 24px rgba(0,0,0,0.08)",
                        marginTop: 6,
                        maxHeight: 260,
                        overflowY: "auto",
                    }}
                >
                    {list.map((opt, i) => (
                        <button
                            key={`${opt}-${i}`}
                            role="option"
                            aria-selected={hi === i}
                            onClick={() => pick(opt)}
                            onMouseEnter={() => setHi(i)}
                            onMouseDown={(e) => e.preventDefault()} // keep focus on input
                            style={{
                                display: "block",
                                width: "100%",
                                textAlign: "left",
                                padding: "8px 10px",
                                border: "none",
                                background: hi === i ? "#f3f4f6" : "transparent",
                                cursor: "pointer",
                            }}
                        >
                            {opt}
                        </button>
                    ))}
                </div>
            )}
        </div>
    );
}
