import React, { useEffect, useMemo, useState } from "react";
import PokemonSelect from "./components/PokemonSelect";
import { dedupeBySpecies, toOptions, humanize, indexById } from "./lib/pokeList";
import { LEAGUE_NAMES, importLeague } from "./Data/leagueFiles";
import gm from "./Data/gamemaster.json";
import { buildMoveBook, simulateDuel, bestOfThree } from "./battleCalc";

const MOVEBOOK = buildMoveBook(gm);

// Simple clickable shield picker
function ShieldPicker({ label, value, onChange }) {
    const btn = (n) => ({
        padding: "6px 12px",
        border: "1px solid #111",
        borderRadius: 8,
        background: value === n ? "#111" : "#fff",
        color: value === n ? "#fff" : "#111",
        cursor: "pointer",
        marginRight: 8,
        minWidth: 36,
        textAlign: "center"
    });
    return (
        <div style={{ display: "inline-flex", alignItems: "center", marginTop: 8, marginRight: 18 }}>
            <span style={{ marginRight: 8 }}>{label}</span>
            {[0, 1, 2].map(n => (
                <button key={n} style={btn(n)} onClick={() => onChange(n)}>{n}</button>
            ))}
        </div>
    );
}

export default function App() {
    const [league, setLeague] = useState(LEAGUE_NAMES[0]);

    const [poolRaw, setPoolRaw] = useState([]);
    const [poolOpts, setPoolOpts] = useState([]);
    const [poolIndex, setPoolIndex] = useState({});

    // YOU: 3 picks
    const [me, setMe] = useState([{ id: "", label: "" }, { id: "", label: "" }, { id: "", label: "" }]);
    // OPPONENT: 3 picks (but we only REQUIRE the first one)
    const [op, setOp] = useState([{ id: "", label: "" }, { id: "", label: "" }, { id: "", label: "" }]);

    const [myShields, setMyShields] = useState(2);
    const [opShields, setOpShields] = useState(2);

    const [results, setResults] = useState(null);
    const [loadError, setLoadError] = useState("");

    // Accept various league JSON shapes
    function normalizeList(modDefault) {
        return Array.isArray(modDefault) ? modDefault
            : Array.isArray(modDefault?.pokemon) ? modDefault.pokemon
                : Array.isArray(modDefault?.data) ? modDefault.data
                    : Array.isArray(modDefault?.list) ? modDefault.list
                        : [];
    }

    // Load league list on league change
    useEffect(() => {
        setLoadError("");
        setResults(null);
        setMe([{ id: "", label: "" }, { id: "", label: "" }, { id: "", label: "" }]);
        setOp([{ id: "", label: "" }, { id: "", label: "" }, { id: "", label: "" }]);

        importLeague(league)
            .then(mod => {
                const list = normalizeList(mod.default);
                const clean = dedupeBySpecies(list);
                setPoolRaw(clean);
                setPoolOpts(toOptions(clean).map(o => o.label));
                setPoolIndex(indexById(clean));
            })
            .catch(err => setLoadError(String(err)));
    }, [league]);

    // map typed label → selection object
    function selectFromLabel(current, setFn, i, typedLabel) {
        const byLabel = poolRaw.find(x => humanize(x.speciesId) === typedLabel);
        const byId = poolRaw.find(x => x.speciesId === typedLabel);
        const picked = byLabel || byId;
        const next = [...current];
        next[i] = picked ? { id: picked.speciesId, label: humanize(picked.speciesId) } : { id: "", label: typedLabel };
        setFn(next);
    }

    // READY = all 3 of yours chosen AND enemy #1 chosen (enemy #2 and #3 are optional for now)
    const ready = useMemo(
        () => me.every(x => !!x.id) && !!op[0]?.id,
        [me, op]
    );

    // INSTANT update: recompute any time picks or shields change
    useEffect(() => {
        if (!ready) { setResults(null); return; }

        const map = poolIndex;
        const myTeam = me.map(s => ({ ...map[s.id], name: s.label }));
        const enemy1 = { ...map[op[0].id], name: op[0].label };

        // Rank your 3 vs the FIRST enemy only (fast, live)
        const { best, fights } = bestOfThree(myTeam, enemy1, myShields ?? 2, opShields ?? 2, MOVEBOOK);

        setResults({
            enemy: enemy1.name,
            bestPick: best.you,
            yourRecommended: best.aRecommended,
            fights // detailed results (your 3 vs enemy #1)
        });
    }, [ready, me, op, myShields, opShields, poolIndex]);

    const h1 = { textAlign: "center", fontSize: 34, fontWeight: 800, margin: "28px 0" };
    const box = { border: "1px solid #e5e7eb", borderRadius: 12, padding: 16, marginBottom: 18, background: "#fff" };
    const colWrap = { display: "grid", gridTemplateColumns: "1fr 1fr", gap: 24, marginTop: 12 };
    const small = { fontSize: 13, color: "#6b7280" };

    return (
        <div style={{ maxWidth: 980, margin: "0 auto", padding: "20px 16px", fontFamily: "system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif" }}>
            <h1 style={h1}>Nebsoji PvP Trainer</h1>

            {/* League + Shields */}
            <div style={box}>
                <label style={{ fontWeight: 600, marginRight: 10 }}>Select League:</label>
                <select value={league} onChange={(e) => setLeague(e.target.value)}>
                    {LEAGUE_NAMES.map(l => <option key={l} value={l}>{l}</option>)}
                </select>

                <div style={{ marginTop: 12, display: "flex", flexWrap: "wrap", alignItems: "center" }}>
                    <ShieldPicker label="Your Shields:" value={myShields} onChange={setMyShields} />
                    <ShieldPicker label="Opponent Shields:" value={opShields} onChange={setOpShields} />
                </div>

                {loadError && <div style={{ color: "#b91c1c", marginTop: 8, fontSize: 13 }}>Failed to load league data: {loadError}</div>}
                <div style={small}>Instant updates — no button. Uses move power/energy from <code>gamemaster.json</code> with basic shields.</div>
            </div>

            {/* Pickers */}
            <div style={colWrap}>
                {/* You: 3 */}
                <div style={box}>
                    <h3 style={{ margin: "0 0 12px 0" }}>Your Team (3)</h3>
                    {me.map((p, i) => (
                        <PokemonSelect
                            key={`me-${i}`}
                            id={`me-${i}`}
                            label={`Pok\u00E9mon ${i + 1}`}
                            options={poolOpts}
                            value={p.label}
                            onChange={(val) => selectFromLabel(me, setMe, i, val)}
                            placeholder="Start typing…"
                        />
                    ))}
                </div>

                {/* Opponents: 3 (only #1 is required for the live calc) */}
                <div style={box}>
                    <h3 style={{ margin: "0 0 12px 0" }}>Opponents (enter #1 now, #2/3 later)</h3>
                    {op.map((p, i) => (
                        <PokemonSelect
                            key={`op-${i}`}
                            id={`op-${i}`}
                            label={`Opponent ${i + 1}${i === 0 ? " (required)" : ""}`}
                            options={poolOpts}
                            value={p.label}
                            onChange={(val) => selectFromLabel(op, setOp, i, val)}
                            placeholder="Start typing…"
                        />
                    ))}
                    <div style={small}>Live results show for Opponent 1 as soon as all 3 of yours + enemy #1 are set.</div>
                </div>
            </div>

            {/* Results (for enemy #1) */}
            {results && (
                <div style={{ ...box, marginTop: 18 }}>
                    <h3 style={{ marginTop: 0 }}>Live Result vs <b>{results.enemy}</b></h3>

                    <div style={{ marginBottom: 10 }}>
                        <div><b>Best pick:</b> {results.bestPick}</div>
                        <div style={small}>Recommended charged (for that pick): <b>{results.yourRecommended || "—"}</b></div>
                    </div>

                    <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr 1fr", gap: 12 }}>
                        {results.fights.map((o, i) => (
                            <div key={i} style={{ border: "1px solid #e5e7eb", borderRadius: 10, padding: 12 }}>
                                <div style={{ fontWeight: 700 }}>{o.you}</div>
                                <div style={small}>vs {results.enemy}</div>
                                <div style={{ marginTop: 6 }}>
                                    Outcome: <b>{o.winner === o.you ? "Win" : (o.winner === "Draw" ? "Draw" : "Loss")}</b>
                                </div>
                                <div style={small}>HP: you {o.aHP} • foe {o.bHP}</div>
                                <div style={small}>Your rec. charged: <b>{o.aRecommended || "—"}</b></div>
                                {o.summary?.length ? (
                                    <div style={{ marginTop: 8 }}>
                                        <div style={{ fontWeight: 600, fontSize: 13 }}>Summary</div>
                                        <ul style={{ margin: "6px 0 0 16px", padding: 0, fontSize: 13, color: "#374151" }}>
                                            {o.summary.map((s, idx) => <li key={idx}>{s}</li>)}
                                        </ul>
                                    </div>
                                ) : null}
                            </div>
                        ))}
                    </div>

                    <div style={{ ...small, marginTop: 10 }}>
                        Quick sim (power/energy only). Types/STAB/CP timing can be added later without changing this UI.
                    </div>
                </div>
            )}
        </div>
    );
}
