// src/App.js
import React, { useEffect, useMemo, useState } from "react";
import PokemonSelect from "./components/PokemonSelect";
import { dedupeBySpecies, toOptions, humanize, indexById } from "./lib/pokeList";
import { LEAGUE_NAMES, importLeague } from "./Data/leagueFiles";
import gm from "./Data/gamemaster.json";          // <-- add this
import { buildMoveBook, bestOfThree } from "./battleCalc"; // <-- add this

export default function App() {
    const [league, setLeague] = useState(LEAGUE_NAMES[0]);

    const [poolRaw, setPoolRaw] = useState([]);     // raw list for selected league
    const [poolOpts, setPoolOpts] = useState([]);   // labels for datalist
    const [poolIndex, setPoolIndex] = useState({}); // speciesId -> entry

    const [me, setMe] = useState([{ id: "", label: "" }, { id: "", label: "" }, { id: "", label: "" }]);
    const [op, setOp] = useState([{ id: "", label: "" }, { id: "", label: "" }, { id: "", label: "" }]);

    const [results, setResults] = useState(null);
    const [loadError, setLoadError] = useState("");

    // Extract array from many possible shapes
    function normalizeList(modDefault) {
        // common shapes: [], { pokemon: [] }, { data: [] }, { list: [] }
        const cand =
            Array.isArray(modDefault) ? modDefault :
                Array.isArray(modDefault?.pokemon) ? modDefault.pokemon :
                    Array.isArray(modDefault?.data) ? modDefault.data :
                        Array.isArray(modDefault?.list) ? modDefault.list :
                            [];
        return cand;
    }

    // Load league JSON from src/Data via dynamic import (no network)
    useEffect(() => {
        setLoadError("");
        setResults(null);
        setMe([{ id: "", label: "" }, { id: "", label: "" }, { id: "", label: "" }]);
        setOp([{ id: "", label: "" }, { id: "", label: "" }, { id: "", label: "" }]);

        importLeague(league)
            .then(mod => {
                const list = normalizeList(mod.default);
                const clean = dedupeBySpecies(list);
                const opts = toOptions(clean).map(o => o.label);

                // Debug once in console to confirm counts
                // console.log(league, { rawLen: list.length, cleanLen: clean.length, sample: clean[0] });

                setPoolRaw(clean);
                setPoolOpts(opts);
                setPoolIndex(indexById(clean));
            })
            .catch(err => setLoadError(String(err)));
    }, [league]);

    const canCalc = useMemo(() => me.every(x => x.id) && op.every(x => x.id), [me, op]);

    function selectFromLabel(current, setFn, i, typedLabel) {
        const byLabel = poolRaw.find(x => humanize(x.speciesId) === typedLabel);
        const byId = poolRaw.find(x => x.speciesId === typedLabel);
        const picked = byLabel || byId;
        const next = [...current];
        next[i] = picked ? { id: picked.speciesId, label: humanize(picked.speciesId) } : { id: "", label: typedLabel };
        setFn(next);
        setResults(null);
    }

    function onCalculate() {
        const map = poolIndex;
        const myTeam = me.map(s => ({ ...map[s.id], name: s.label }));
        const foeTeam = op.map(s => ({ ...map[s.id], name: s.label }));
        setResults({ myTeam, foeTeam });
    }

    const h1 = { textAlign: "center", fontSize: 34, fontWeight: 800, margin: "28px 0" };
    const box = { border: "1px solid #e5e7eb", borderRadius: 12, padding: 16, marginBottom: 18, background: "#fff" };
    const colWrap = { display: "grid", gridTemplateColumns: "1fr 1fr", gap: 24, marginTop: 12 };

    return (
        <div style={{ maxWidth: 980, margin: "0 auto", padding: "20px 16px", fontFamily: "system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif" }}>
            <h1 style={h1}>Nebsoji PvP Trainer</h1>

            <div style={box}>
                <label style={{ fontWeight: 600, marginRight: 10 }}>Select League:</label>
                <select value={league} onChange={(e) => setLeague(e.target.value)}>
                    {LEAGUE_NAMES.map(l => <option key={l} value={l}>{l}</option>)}
                </select>
                {loadError && (
                    <div style={{ color: "#b91c1c", marginTop: 8, fontSize: 13 }}>
                        Failed to load league data: {loadError}
                    </div>
                )}
            </div>

            <div style={colWrap}>
                <div style={box}>
                    <h3 style={{ margin: "0 0 12px 0" }}>Your Team</h3>
                    {me.map((p, i) => (
                        <PokemonSelect
                            key={`me-${i}`}
                            id={`me-${i}`}
                            label={`Pok\u00E9mon ${i + 1}`}  // ensures ï¿½ renders even if file encoding flips
                            options={poolOpts}
                            value={p.label}
                            onChange={(val) => selectFromLabel(me, setMe, i, val)}
                            placeholder="Start typingï¿½"
                        />
                    ))}
                </div>

                <div style={box}>
                    <h3 style={{ margin: "0 0 12px 0" }}>Opponent Team</h3>
                    {op.map((p, i) => (
                        <PokemonSelect
                            key={`op-${i}`}
                            id={`op-${i}`}
                            label={`Opponent ${i + 1}`}
                            options={poolOpts}
                            value={p.label}
                            onChange={(val) => selectFromLabel(op, setOp, i, val)}
                            placeholder="Start typingï¿½"
                        />
                    ))}
                </div>
            </div>

            <button
                disabled={!canCalc}
                onClick={onCalculate}
                style={{
                    padding: "10px 18px",
                    marginTop: 12,
                    borderRadius: 10,
                    border: "1px solid #111",
                    background: canCalc ? "#111" : "#aaa",
                    color: "#fff",
                    cursor: canCalc ? "pointer" : "not-allowed"
                }}
            >
                Calculate Matchups
            </button>

            {results && (
                <div style={{ ...box, marginTop: 18 }}>
                    <h3 style={{ marginTop: 0 }}>Results</h3>
                    <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 24 }}>
                        <div>
                            <h4 style={{ margin: "6px 0" }}>Your Team</h4>
                            {results.myTeam.map((m, idx) => (
                                <div key={`my-${idx}`} style={{ padding: "8px 0", borderBottom: "1px solid #eee" }}>
                                    <div style={{ fontWeight: 600 }}>{m.name}</div>
                                    <div style={{ fontSize: 13, color: "#6b7280" }}>
                                        {m?.fastMove ? `Fast: ${m.fastMove}` : "Fast: ï¿½"}
                                        {Array.isArray(m?.chargedMoves) ? ` ï¿½ Charged: ${m.chargedMoves.join(", ")}` : ""}
                                    </div>
                                </div>
                            ))}
                        </div>
                        <div>
                            <h4 style={{ margin: "6px 0" }}>Opponent Team</h4>
                            {results.foeTeam.map((m, idx) => (
                                <div key={`foe-${idx}`} style={{ padding: "8px 0", borderBottom: "1px solid #eee" }}>
                                    <div style={{ fontWeight: 600 }}>{m.name}</div>
                                    <div style={{ fontSize: 13, color: "#6b7280" }}>
                                        {m?.fastMove ? `Fast: ${m.fastMove}` : "Fast: ï¿½"}
                                        {Array.isArray(m?.chargedMoves) ? ` ï¿½ Charged: ${m.chargedMoves.join(", ")}` : ""}
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
