import React, { useMemo, useState, useEffect } from "react";

export default function PokemonSelect({
    id,
    label,
    options = [],          // array of strings, e.g. "Dialga", "Dialga (Origin)", "Reshiram"
    value = "",
    onChange,              // (label: string) => void
    placeholder = "Start typing…",
    maxSuggestions = 5,
}) {
    const [term, setTerm] = useState("");
    const [editing, setEditing] = useState(false);

    // keep local buffer in sync if parent clears value
    useEffect(() => {
        if (!value && !editing) setTerm("");
    }, [value, editing]);

    // Base name = strip everything after " ("
    // e.g. "Dialga (Origin)" -> "Dialga", "Zacian (Crowned Sword)" -> "Zacian"
    // Keeps hyphenated names like "Ho-Oh" intact.
    function baseName(label) {
        return String(label || "").split(" (")[0].trim();
    }

    const norm = (s) => String(s || "").toLowerCase();

    // Show suggestions only when typing, and match by base name prefix
    const suggestions = useMemo(() => {
        const q = norm(term).trim();
        if (!q) return [];
        return options
            .filter((opt) => norm(baseName(opt)).startsWith(q))
            .slice(0, maxSuggestions);
    }, [options, term, maxSuggestions]);

    // what to show in the input
    const inputValue = editing ? term : value;

    function accept(label) {
        if (!label) return;
        onChange?.(label);
        setEditing(false);
        setTerm(""); // parent now controls the displayed value
    }

    function handleChange(e) {
        const v = e.target.value;
        // user selected an exact option from the datalist
        if (options.includes(v)) {
            accept(v);
            return;
        }
        // otherwise, keep typing
        setEditing(true);
        setTerm(v);
    }

    // quick-accept the single suggestion on Enter/Tab
    function handleKeyDown(e) {
        if ((e.key === "Enter" || e.key === "Tab") && suggestions.length === 1) {
            e.preventDefault();
            accept(suggestions[0]);
        }
    }

    function handleBlur() {
        setEditing(false);
        setTerm("");
    }

    return (
        <div style={{ marginBottom: 12 }}>
            {label && (
                <label htmlFor={id} style={{ display: "block", marginBottom: 6 }}>
                    {label}
                </label>
            )}

            <input
                id={id}
                list={`${id}-list`}
                value={inputValue}
                placeholder={placeholder}
                onChange={handleChange}
                onKeyDown={handleKeyDown}
                onFocus={() => setEditing(true)}
                onBlur={handleBlur}
                autoComplete="off"
                style={{
                    width: 320,                // keeps away from the ✖ button
                    padding: 8,
                    border: "1px solid #d1d5db",
                    borderRadius: 8,
                }}
            />

            {/* native suggestions; only visible while typing */}
            <datalist id={`${id}-list`}>
                {suggestions.map((name) => (
                    <option key={name} value={name} />
                ))}
            </datalist>
        </div>
    );
}
