// src/components/PokemonSelect.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";

export default function PokemonSelect({
    id,
    label,
    options = [],
    value = "",
    onChange,
    placeholder = "Start typing�",
    maxSuggestions = 20,
    rightInsetPx = 28,   // leaves space for your purple X button
}) {
    const [open, setOpen] = useState(false);
    const [term, setTerm] = useState(value || "");
    const [hi, setHi] = useState(-1);
    const wrapRef = useRef(null);
    const inputRef = useRef(null);

    // keep in sync with parent
    useEffect(() => { setTerm(value || ""); setHi(-1); }, [value]);

    const norm = (s) => String(s || "").toLowerCase().trim();
    // STRICT prefix-only (whole label)
    const list = useMemo(() => {
        const q = norm(term);
        if (!q) return options.slice(0, maxSuggestions);
        return options.filter((opt) => norm(opt).startsWith(q)).slice(0, maxSuggestions);
    }, [options, term, maxSuggestions]);

    // click-away
    useEffect(() => {
        function onDocClick(e) {
            if (!wrapRef.current) return;
            if (!wrapRef.current.contains(e.target)) setOpen(false);
        }
        document.addEventListener("mousedown", onDocClick);
        return () => document.removeEventListener("mousedown", onDocClick);
    }, []);

    function pick(v) {
        setTerm(v);
        setOpen(false);
        setHi(-1);
        onChange?.(v);
    }

    function onKeyDown(e) {
        if (!open && (e.key === "ArrowDown" || e.key === "ArrowUp")) { setOpen(true); return; }
        if (e.key === "ArrowDown") { e.preventDefault(); setHi((i) => (list.length ? (i + 1) % list.length : -1)); }
        else if (e.key === "ArrowUp") { e.preventDefault(); setHi((i) => (list.length ? (i - 1 + list.length) % list.length : -1)); }
        else if (e.key === "Enter") { if (open && list.length && hi >= 0) { e.preventDefault(); pick(list[hi]); } }
        else if (e.key === "Escape") { setOpen(false); setHi(-1); }
    }

    return (
        <div ref={wrapRef} style={{ position: "relative" }}>
            {label && (
                <label htmlFor={id} style={{ display: "block", fontSize: 13, color: "#374151", marginBottom: 4 }}>
                    {label}
                </label>
            )}

            <input
                id={id}
                ref={inputRef}
                type="text"
                value={term}
                placeholder={placeholder}
                onFocus={() => setOpen(true)}
                onKeyDown={onKeyDown}
                onChange={(e) => { setTerm(e.target.value); setOpen(true); setHi(-1); }}
                autoComplete="off"
                spellCheck={false}
                style={{
                    width: "100%",
                    border: "1px solid #e5e7eb",
                    borderRadius: 8,
                    padding: `8px ${rightInsetPx + 10}px 8px 10px`, // reserve room for the X
                    outline: "none",
                    background: "#fff",
                }}
            />

            {open && list.length > 0 && (
                <div
                    role="listbox"
                    style={{
                        position: "absolute",
                        zIndex: 12,
                        top: "100%",
                        left: 0,
                        right: rightInsetPx,     // don't sit under the X
                        background: "#fff",
                        border: "1px solid #e5e7eb",
                        borderRadius: 8,
                        boxShadow: "0 8px 24px rgba(0,0,0,0.08)",
                        marginTop: 6,
                        maxHeight: 260,
                        overflowY: "auto",
                    }}
                >
                    {list.map((opt, i) => (
                        <button
                            key={`${opt}-${i}`}
                            role="option"
                            aria-selected={hi === i}
                            onClick={() => pick(opt)}
                            onMouseEnter={() => setHi(i)}
                            onMouseDown={(e) => e.preventDefault()} // keep focus on input
                            style={{
                                display: "block",
                                width: "100%",
                                textAlign: "left",
                                padding: "8px 10px",
                                border: "none",
                                background: hi === i ? "#f3f4f6" : "transparent",
                                cursor: "pointer",
                            }}
                        >
                            {opt}
                        </button>
                    ))}
                </div>
            )}
        </div>
    );
}
