// src/components/PokemonSelect.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";

export default function PokemonSelect({
    id,
    label,
    options = [],
    value = "",
    onChange,
    placeholder = "Start typing�",
    maxSuggestions = 8,
}) {
    const [open, setOpen] = useState(false);
    const [term, setTerm] = useState(value || "");
    const wrapRef = useRef(null);

    // keep controlled value in sync if parent updates it
    useEffect(() => setTerm(value || ""), [value]);

    // ---- prefix-only matcher ----
    function norm(s) {
        return String(s || "").toLowerCase();
    }
    function startsAtWord(label, q) {
        const L = norm(label);
        const Q = norm(q).trim();
        if (!Q) return true;
        // whole-label startsWith
        if (L.startsWith(Q)) return true;
        // word-boundary startsWith (split on spaces, dashes, underscores, parens)
        const words = L.split(/[\s\-\(\)_]+/g).filter(Boolean);
        return words.some((w) => w.startsWith(Q));
    }

    const list = useMemo(() => {
        const q = term.trim();
        if (!q) return options.slice(0, maxSuggestions);
        return options.filter((opt) => startsAtWord(opt, q)).slice(0, maxSuggestions);
    }, [options, term, maxSuggestions]);

    // click-away to close
    useEffect(() => {
        function onDocClick(e) {
            if (!wrapRef.current) return;
            if (!wrapRef.current.contains(e.target)) setOpen(false);
        }
        document.addEventListener("mousedown", onDocClick);
        return () => document.removeEventListener("mousedown", onDocClick);
    }, []);

    function pick(v) {
        setTerm(v);
        setOpen(false);
        onChange?.(v);
    }

    return (
        <div ref={wrapRef} style={{ position: "relative" }}>
            {label && (
                <label htmlFor={id} style={{ display: "block", fontSize: 13, color: "#374151", marginBottom: 4 }}>
                    {label}
                </label>
            )}

            <input
                id={id}
                type="text"
                value={term}
                placeholder={placeholder}
                onFocus={() => setOpen(true)}
                onChange={(e) => {
                    setTerm(e.target.value);
                    setOpen(true);
                    // don�t call onChange here to avoid picking mid-typing strings
                }}
                style={{
                    width: "100%",
                    border: "1px solid #d1d5db",
                    borderRadius: 8,
                    padding: "8px 10px",
                    outline: "none",
                }}
            />

            {open && list.length > 0 && (
                <div
                    style={{
                        position: "absolute",
                        zIndex: 20,
                        top: "100%",
                        left: 0,
                        right: 0,
                        background: "#fff",
                        border: "1px solid #e5e7eb",
                        borderRadius: 8,
                        boxShadow: "0 8px 24px rgba(0,0,0,0.08)",
                        marginTop: 6,
                        maxHeight: 260,
                        overflowY: "auto",
                    }}
                >
                    {list.map((opt, i) => (
                        <button
                            key={`${opt}-${i}`}
                            onClick={() => pick(opt)}
                            style={{
                                display: "block",
                                width: "100%",
                                textAlign: "left",
                                padding: "8px 10px",
                                border: "none",
                                background: "transparent",
                                cursor: "pointer",
                            }}
                            onMouseDown={(e) => e.preventDefault()} // keep focus so input value stays
                        >
                            {opt}
                        </button>
                    ))}
                </div>
            )}
        </div>
    );
}
