// src/components/PokemonSelect.jsx
import React, { useMemo } from "react";

export default function PokemonSelect({
    id,
    label,
    options = [],        // array of strings (labels)
    value = "",
    onChange,            // (typedString) => void
    placeholder = "Start typing�",
    maxSuggestions = 20, // limit suggestions
}) {
    const norm = (s) => String(s || "").toLowerCase();
    const q = norm(value).trim();

    // Only show options that START WITH the typed text
    const filtered = useMemo(() => {
        if (!q) return options.slice(0, maxSuggestions);
        return options
            .filter((name) => norm(name).startsWith(q))
            .slice(0, maxSuggestions);
    }, [options, q, maxSuggestions]);

    return (
        <div style={{ marginBottom: 12 }}>
            {label && (
                <label htmlFor={id} style={{ display: "block", marginBottom: 6 }}>
                    {label}
                </label>
            )}

            <input
                id={id}
                list={`${id}-list`}
                value={value}
                placeholder={placeholder}
                onChange={(e) => onChange?.(e.target.value)}
                autoComplete="off"
                style={{
                    padding: 8,
                    width: "100%",
                    border: "1px solid #e5e7eb",
                    borderRadius: 8,
                }}
            />

            <datalist id={`${id}-list`}>
                {filtered.map((name) => (
                    <option key={name} value={name} />
                ))}
            </datalist>
        </div>
    );
}
