// src/engine/battleEngine.js
// Normalizes whatever PokemonBattle exports into a single named export: simulateBattle

import defExport, * as ns from "../PokemonBattle";

const simulateBattle =
    // default export is a function
    (typeof defExport === "function" && defExport) ||
    // default export is an object with simulateBattle
    (defExport && typeof defExport.simulateBattle === "function" && defExport.simulateBattle) ||
    // named export simulateBattle
    (typeof ns.simulateBattle === "function" && ns.simulateBattle);

if (!simulateBattle) {
    throw new Error(
        "PokemonBattle does not export simulateBattle (default or named). " +
        "Make sure PokemonBattle.js exports either `export default function(...)` " +
        "or `export function simulateBattle(...)` or `export default { simulateBattle }`."
    );
}

export { simulateBattle };
