// src/battleCalc.js
// Thin adapter your App uses. It calls the battle engine and returns the shape App expects.

import { simulateBattle } from "./engine/battleEngine";

let SPECIES = {};
let MOVES = {};

// ---- tiny helpers kept for bootstrap compatibility ----
const num = (v, d = 0) => (Number.isFinite(Number(v)) ? Number(v) : d);
const canonMoveId = (s) => String(s || "")
    .trim()
    .replace(/^COMBAT_V\d+_MOVE_/i, "")
    .replace(/^V\d+_/i, "")
    .replace(/[^A-Za-z0-9]+/g, "_")
    .replace(/_+/g, "_")
    .replace(/^_|_$/g, "")
    .toUpperCase();
const normId = (s) => String(s || "").toLowerCase().replace(/[^\w]+/g, "_").replace(/^_+|_+$/g, "");
function capLeague(league) {
    const map = { "Great League": 1500, "Ultra League": 2500, "Master League": Infinity };
    return typeof league === "string" ? (map[league] ?? Infinity) : (Number.isFinite(league) ? Number(league) : Infinity);
}

// CPM helpers
const CPM = []; (function fillCPM() {
    const table = [0.094, 0.135137432, 0.16639787, 0.192650919, 0.21573247, 0.236572661, 0.25572005, 0.273530381, 0.29024988, 0.306057377, 0.3210876, 0.335445036, 0.34921268, 0.362457751, 0.37523559, 0.387592406, 0.39956728, 0.411193551, 0.42250001, 0.432926419, 0.44310755, 0.4530599578, 0.46279839, 0.472336083, 0.48168495, 0.4908558, 0.49985844, 0.508701765, 0.51739395, 0.525942511, 0.53435433, 0.542635767, 0.55079269, 0.558830576, 0.56675452, 0.574569153, 0.58227891, 0.589887907, 0.59740001, 0.604818814, 0.61215729, 0.619399365, 0.62656713, 0.633644533, 0.64065295, 0.647576426, 0.65443563, 0.661214806, 0.667934, 0.674577537, 0.68116492, 0.687680648, 0.69414365, 0.700538673, 0.70688421, 0.713164996, 0.71939909, 0.725571552, 0.7317, 0.734741009, 0.73776948, 0.740785574, 0.74378943, 0.746781211, 0.74976104, 0.752729087, 0.75568551, 0.758630378, 0.76156384, 0.764486065, 0.76739717, 0.770297266, 0.7731865, 0.776064962, 0.77893275, 0.781790055, 0.78463697, 0.787473578, 0.79030001, 0.792803968, 0.79530001, 0.797803921, 0.8003];
    CPM.length = 100; CPM.fill(0); for (let i = 0; i < table.length; i++) CPM[i + 1] = table[i];
})();
function cp(a, d, s, c) { return Math.floor((a * Math.sqrt(d) * Math.sqrt(s) * c * c) / 10); }
function levelForCap(baseAtk, baseDef, baseSta, cap) {
    if (!isFinite(cap)) return 50;
    let best = 1; for (let i = 1; i < CPM.length; i++) { const c = CPM[i]; if (!c) continue; if (cp(baseAtk * c, baseDef * c, baseSta * c, 1) <= cap) best = i; }
    return best;
}

// Build species/moves from gamemaster (used by App bootstrap)
function buildSpeciesBook(gm) {
    const lists = [gm?.pokemon, gm?.data?.pokemon].filter(Boolean);
    const out = {};
    for (const L of lists) {
        for (const p of L) {
            const id = normId(p.speciesId || p.id || p.name);
            const bs = p.baseStats || p.stats || {};
            const typesArr = p.types || [p.type1, p.type2].filter(Boolean);
            const types = (typesArr || []).map(t => String(t).replace(/^POKEMON_TYPE_/, "").toLowerCase());
            out[id] = {
                atk: num(bs.atk ?? bs.attack, 200),
                def: num(bs.def ?? bs.defense, 200),
                sta: num(bs.hp ?? bs.sta ?? bs.stamina, 200),
                types: types.length ? types : ["normal"]
            };
        }
    }
    return out;
}

export function buildMoveBook(gm) {
    SPECIES = buildSpeciesBook(gm);
    const pools = [gm?.moves, gm?.data?.moves, gm?.combatMoves, gm?.data?.combatMoves].filter(Boolean);
    const out = {};
    for (const list of pools) {
        for (const m of list) {
            const id = canonMoveId(m.moveId ?? m.id ?? m.uniqueId ?? m.templateId ?? m.name);
            if (!id) continue;
            const type = String(m.type || m.pokemonType || m.moveType || "Normal").replace(/^POKEMON_TYPE_/, "").toLowerCase();
            const power = num(m.pvpPower ?? m.power ?? m.combatPower ?? m.damage, 3);
            let kind = "fast", energyGain = 0, energyCost = 0, turns = 1;
            if (m.energyDelta != null) { const ed = num(m.energyDelta, 0); if (ed > 0) { kind = "fast"; energyGain = ed; } if (ed < 0) { kind = "charged"; energyCost = Math.abs(ed); } }
